import styled from 'styled-components';
import { NavLink } from 'react-router-dom';

export const Link = styled(NavLink)`
  display: block;
  width: 200px;
  padding: 30px;
  text-decoration: none;
  color: blue;

  &.active {
    color: white;
    background-color: grey;
  }
`;
