import { useEffect } from 'react';
import { useState } from 'react';

export function CartItem({ item: { name, price, id }, setMapedProducts }) {
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    console.log(quantity);
    setMapedProducts(prev => {
      const product = prev.find(product => product.id === id);
      product.quantity = quantity.toString();
      return [...prev, product];
    });
  }, [quantity, id, setMapedProducts]);

  const inputHandler = e => {
    setQuantity(e.target.value);
  };

  return (
    <li>
      <p>{name}</p>
      <p>Price: {price}</p>
      <label>
        Quantity
        <input
          onChange={inputHandler}
          type="number"
          value={quantity}
          min="1"
          max="10"
          step="1"
          name={id}
        />
      </label>
    </li>
  );
}
